static void
remove_suffix (char *name, char const *suffix)
{
  char *np;
  char const *sp;

  np = name + strlen (name);
  sp = suffix + strlen (suffix);

  while (np > name && sp > suffix)
    if (*--np != *--sp)
      return;
  if (np > name)
    *np = '�';
}

